import "./App.css";

import {useState} from "react";
import axios from "axios";

function App() {
  const [selectedFile, setSelectedFile] = useState();

  const handleSubmitFile = (evt) => {
    evt.preventDefault();

    //File Reader Start
    const reader = new FileReader();
    reader.readAsDataURL(selectedFile);
    reader.onloadend = () => {
      uploadImage(reader.result);
    };
    reader.onerror = () => {
      console.error("file reader error");
    };
  };

  const uploadImage = async (base64EncodedImage) => {
    console.log
    try {
      const url = "http://127.0.0.1:8000/photos/upload";
      const image = JSON.stringify({data: base64EncodedImage})
      const headers = { "Content-Type": "application/json"}
      await axios.post(url, image, {headers: headers})

    } catch (err) {
      console.error(err);
    }
  };

  const handleFileInputChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  return (
    <div className="items-container">
      <form onSubmit={handleSubmitFile}>
        <input type="file" onChange={handleFileInputChange} accept="image/x-png,image/gif,image/jpeg"/>
        <button>Submit Photo</button>
      </form>
    </div>
  );
}

export default App;
