import "./App.css";
import axios from "axios";

function App() {

  const handleUpload = (evt) => {
    evt.preventDefault();

    const reader = new FileReader();
    reader.readAsDataURL(evt.target.files[0]);
    reader.onloadend = () => {uploadImageToServer(reader.result)};
  };

  const uploadImageToServer = async (imageCode) => {
    await axios.post("http://127.0.0.1:8000/photos/upload", JSON.stringify({data: imageCode}), {headers: {"Content-Type": "application/json"}});
  };

  return (
    <div className="input-container">
      <div className="input-inner-container">
        <label className="file-uploader">
          <input type="file" onChange={handleUpload}/>
          Custom Upload
        </label>
      </div>
    </div>
  );
}

export default App;
