import "./App.css";

import {useState} from "react";
import axios from "axios";

function App() {
  const [selectedFile, setSelectedFile] = useState();

  const handleSubmitFile = (evt) => {
    evt.preventDefault();

    //File Reader Start
    const reader = new FileReader();
    reader.readAsDataURL(selectedFile);
    reader.onloadend = () => {uploadImage(reader.result)};
  };

  const uploadImage = async (base64EncodedImage) => {
    const url = "http://127.0.0.1:8000/photos/upload";
    const image = JSON.stringify({data: base64EncodedImage});
    const headers = {"Content-Type": "application/json"};

    await axios.post(url, image, {headers: headers});
  };

  const handleFileInputChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  return (
    <div className="items-container">
      <form onSubmit={handleSubmitFile}>
        <input
          type="file"
          onChange={handleFileInputChange}
          accept="image/x-png,image/gif,image/jpeg"
        />
        <button>Submit Photo</button>
      </form>
    </div>
  );
}

export default App;
